        # (self.stack_oldest_on_top == []) was wrong, so I'll remove it

    def _sync_stacks(self):
        while self.stack_newest_on_top:  # Older items to newest
            self.stack_oldest_on_top.append(self.stack_newest_on_top.pop())

    def dequeue(self):
        """Remove the element from the front of the queue"""
        if not self.stack_oldest_on_top:
            self._sync_stacks()  # Move all items to stack_oldest_on_top
        return self.stack_oldest_on_top.pop()

    def is_empty(self):
        return not (self.stack_newest_on_top or self.stack_oldest_on_top)

