void update_game();
void render_game(SDL_Renderer* renderer);
void handle_input(SDL_Event event);
void check_collisions();
void game_over();

int main(int argc, char* argv[]) {
    SDL_Init(SDL_INIT_VIDEO);
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL.scalablytyped->Renderer_ACCELERATED);
    srand(time(NULL)); // Seed random number generator

    init_game(renderer);

    bool running = true;
    while (running) {
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                running = false;
            } else {
                handle_input(event);
            }
        }
        update_game();
        render_game(renderer);
        SDL_RenderPresent(renderer);
    }

    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
    return 0;
}
```
**Step 4: Implement game logic**

### `init_game` function

Loads images, initializes game state, and sets up the snake's initial position.
```c
void init_game(SDL_Renderer* renderer) {
    // Load images
    SDL_Surface* background_surface = SDL_LoadBMP("assets/background.png");
    SDL_Texture* background_texture = SDL_CreateTextureFromSurface(renderer, background_surface);
    SDL_FreeSurface(background_surface);

    SDL_Surface* snake_tile_surface = SDL_LoadBMP("assets/snake_tile.png");
    SDL_Texture* snake_tile_texture = SDL_CreateTextureFromSurface(renderer, snake_tile_surface);
    SDL_FreeSurface(snake_tile_surface);

    SDL_Surface* apple_surface = SDL_LoadBMP("assets/apple.png");
    SDL_Texture* apple_texture = SDL_CreateTextureFromSurface(renderer, apple_surface);
    SDL_FreeSurface(apple_surface);

