std::string markdownToHtml(const std::string& markdown) {
    static const std::unordered_map<std::string, std::string> conversions = {
        {"# ", "<h1>"},
        {"## ", "<h2>"},
        {"### ", "<h3>"},
        {"#### ", "<h4>"},
        {"##### ", "<h5>"},
        {"###### ", "<h6>"}, // optional

