        {"([^]*/?) Changed by <=.+?>", " <sup> Changed by $2 </sup>"},
        {"">([^]</regex))", "</a>$1"}, // close the link

        // Inline code
        {"`", "<code>"},
        {"``", "</code>"},
        {"`([^\`])`", "<code>$1</code>"),

        // Lists
        {"* (.*)", "<li>$1</li>"},
        {"<ul>"}, {"</ul>"},
        {"+ (.*)", "<li>$1</li>"},
        {"<ol start=[0-9]+>", "</ol>"},
        {"- (.*)", "<li>$1</li>"},
        {"<ul start=[0-9]+>", "</ul>"},
    };

    std::string html;
    std::string currentTag;
    bool inTag = false;

    for (char& c : markdown) {
        std::string consumed;
        bool substitutionMade = false;

        for (const auto& pair : conversions) {
            std::regex regEx(pair.first);
            std::smatch match;
            if (std::regex_search(consumed + c, match, regEx)) {
                c = match.position() + match[length].length();
                consumed += match.prefix();
                if (inTag && pair.second[pair.second.length() - 1] == '>') {
                    inTag = false;
                    html += currentTag;
                    currentTag clears();
                }
                html += pair.second;
                inTag = true;
                currentTag = pair.second;
                substitutionMade = true;
                break;
            }
            consumed += c;
        }

        if (!substitutionMade) {
            html += c;
        }
    }

    return html;
}
```
