      const { commandName } = interaction;

      if (commandName === 'ban') {
        const command = client.commands.get('ban') || client.commands.find(c => c.name === 'ban');
        if (!command) return;

        // Define the ban command logic here
        const member = interaction.options.getMember('member');
        const reason = interaction.options.getString('reason', true);

        if (!member) {
          return interaction.reply({ content: 'Invalid member mention!', ephemeral: true });
        }

        // Check if the bot has the necessary permissions
        if (!member.guild.members.cache.get(client.user.id).permissions.has(PermissionsBitField.Flags.BanMembers)) {
          return interaction.reply({ content: 'I don\'t have permission to ban users!', ephemeral: true });
        }

        // Ban the member
