```cpp
#include <iostream>
#include <fstream>
#include <sstream>
#include <string>

int countWordOccurrences(const std::string& filename, const std::string& word) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error opening file!" << std::endl;
        return -1; // return error code
    }

    int count = 0;
    std::string line, wordCopy = word;
    std::transform(wordCopy.begin(), wordCopy.end(), wordCopy.begin(), ::tolower); // convert word to lowercase for case-insensitive search

    while (std::getline(file, line)) {
        std::stringstream lineStream(line);
        std::string token;
        while (std::getline(lineStream, token, ' ')) { // split line into tokens by spaces
            std::string tokenCopy = token;
            std::transform(tokenCopy.begin(), tokenCopy.end(), tokenCopy.begin(), ::tolower); // convert token to lowercase for case-insensitive search
            if (tokenCopy == wordCopy)
                ++count;
        }
    }

    file.close();
    return count;
}

int main() {
    std::string filename = "example.txt"; // replace with your file name
    std::string word = "hello"; // replace with the word you want to count

    int count = countWordOccurrences(filename, word);
