        self.stack_new_elements = []  # stack for adding elements
        self.stack_old_elements = []   # stack for removing elements

    def enqueue(self, item):
        # Add an element to the queue (enqueue)
        self.stack_new_elements.append(item)

    def dequeue(self):
        # Remove an element from the queue (dequeue)
        if not self.stack_old_elements:
            # If stack_old_elements is empty, move all elements from stack_new_elements to stack_old_elements
            while self.stack_new_elements:
                self.stack_old_elements.append(self.stack_new_elements.pop())
        if self.stack_old_elements:
            return self.stack_old_elements.pop()
        else:
            return None

    def is_empty(self):
        # Check if the queue is empty
        return len(self.stack_new_elements) == 0 and len(self.stack_old_elements) == 0

    def size(self):
        # Get the size of the queue
        return len(self.stack_new_elements) + len(self.stack_old_elements)


