  // Use a custom reporter to save screenshots
  config.reporter = 'custom-reporter';

  return config;
};

// Custom reporter to save screenshots
const customReporter = (options) => {
  const { parent } = options;
  return {
    onTestHeaderCode: (test, context, { formattedText }) => {
      // Save screenshot on test completion
      Cypress.runner.on('test:complete', (test) => {
        if (test.state === 'passed' || test.state === 'failed') {
          Cypress.browser().capture('screenshot', 'test-screenshot').then((url) => {
            const screenshotPath = path.join(parent.config.screenshotFolder, `${test.title}.png`);
