Here's a simple C++ program that reads a text file line by line and counts the number of occurrences of a specific word:

```cpp
#include <iostream>
#include <fstream>
#include <sstream>
#include <string>

using namespace std;

int countWordOccurrences(string filename, string word) {
    int count = 0;
    string line, temp;

    ifstream file(filename);
    if (!file.is_open()) {
        cerr << "Error opening file" << endl;
        return -1;
    }

    while (getline(file, line)) {
        stringstream ss(line);
        while (getline(ss, temp, ' ')) {
            if (temp == word) {
                count++;
            }
        }
    }
    file.close();
    return count;
}

int main() {
    string filename, word;
    cout << "Enter the filename: ";
    cin >> filename;
    cout << "Enter the word to search: ";
    cin >> word;

    int count = countWordOccurrences(filename, word);
    if (count != -1) {
        cout << "The word '" << word << "' occurs " << count << " times in the file." << endl;
    }
    return 0;
