def fibonacci(n):
    if n <= 0:
        return "Input should be a positive integer."
    elif n == 1:
        return 0
    elif n == 2:
        return 1
    else:
        fib_values = [0, 1]
        for i in range(2, n):
            fib_values.append(fib_values[i-1] + fib_values[i-2])
