Here's a simple C++ program that reads a text file line by line and counts the number of occurrences of a specific word in the file:

```cpp
#include <iostream>
#include <fstream>
#include <sstream>
#include <string>
#include <map>

using namespace std;

int countWordOccurrences(const string& filename, const string& targetWord) {
    int count = 0;
    map<string, int> wordCounts;

    ifstream file(filename);
    if (!file.is_open()) {
        cout << "Error opening file." << endl;
        return -1;
    }

    string line, word;
    while (getline(file, line)) {
        stringstream lineStream(line);
        while (getline(lineStream, word, ' ')) {
            word = word.substr(0, word.find('\0')); // remove trailing null character
            ++wordCounts[word];
        }
    }

    file.close();

    if (wordCounts.find(targetWord) != wordCounts.end())
        count = wordCounts[targetWord];

    return count;
}

int main() {
    string filename = "input.txt"; // replace with your file name
    string targetWord = "hello"; // replace with the word you want to count

    int occurrences = countWordOccurrences(filename, targetWord);

    if (occurrences != -1) {
        cout << "The word '" << targetWord << "' occurs " << occurrences << " times in the file." << endl;
    }

    return 0;
