#include <iostream>
#include <fstream>
#include <sstream>
#include <string>
#include <map>

using namespace std;

int countWordOccurrences(const string& filename, const string& word) {
    int count = 0;
    ifstream file(filename);
    string line;

    if (file.is_open()) {
        while (getline(file, line)) {
            istringstream iss(line);
            string currentWord;
            while (iss >> currentWord) {
                if (currentWord == word) {
                    count++;
                }
            }
        }
        file.close();
    } else {
        cout << "Unable to open file";
        return -1;
    }

    return count;
}

int main() {
    string filename, word;
    cout << "Enter the filename: ";
    cin >> filename;
    cout << "Enter the word to count: ";
    cin >> word;

    int count = countWordOccurrences(filename, word);
    if (count != -1) {
        cout << "The word '" << word << "' occurs " << count << " times in the file." << endl;
    }

    return 0;
}
```

