#include <regex>
#include <boost/algorithm/string/replace.hpp>
#include <boost/regex.hpp>

std::string convertMarkdownToHTML(const std::string& markdown) {
    std::string html;

    // Bold
    html = boost::replace_all(html, "**", "<b>");
    html = boost::replace_all(html, "**", "</b>");

    // Italic
    html = boost::replace_all(html, "*", "<i>");
    html = boost::replace_all(html, "*", "</i>");

    // Headers
    html = boost::replace_all(html, "# ", "<h1>");
    html = boost::replace_all(html, "## ", "<h2>");
    html = boost::replace_all(html, "### ", "<h3>");
    html = boost::replace_all(html, "#### ", "<h4>");
    html = boost::replace_all(html, "##### ", "<h5>");
    html = boost::replace_all(html, "###### ", "<h6>");
    html = boost::replace_all(html, "# ", "</h1>");

    // Links
