    html = boost::replace_all(html, "]", "'>");
    html = boost::replace_all(html, "]", "</a>");
    html = boost::replace_all(html, "(| )", "</a>");

    // Lists
    html = boost::replace_all(html, "\* ", "<ul><li>");
    html = boost::replace_all(html, "  \* ", "</li><li>");
    html = boost::replace_all(html, "\n", "</li></ul>");
    html = boost::replace_all(html, "\n", "<ol><li>");
    html = boost::replace_all(html, "  \n", "</li><li>");
    html = boost::replace_all(html, "\n", "</li></ol>");

    // Images
    html = boost::replace_all(html, "!\[", "<img src='");
    html = boost::replace_all(html, "\(", "'");
    html = boost::replace_all(html, "\]", "' />");

    return html;
}
```

