// Initialize the game
void init_game() {
    // Initialize SDL
    SDL_Init(SDL_INIT_VIDEO);
    // Create a window
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, GAME_WIDTH * 20, GAME_HEIGHT * 20, SDL_WINDOW_SHOWN);
    // Create a renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    // Load images
    load_images(renderer);
    // Initialize snake and score
    snake.head = (Point*)malloc(sizeof(Point));
    snake.head->x = 5;
    snake.head->y = 5;
    snake.tail = snake.head + 1;
    snake.length = SNAKE_LENGTH;
    score = 0;
    game_over = false;
}

// Update game logic
void update_game() {
    // Handle user input
    handle_input();
    // Move snake
