// Move snake
void move_snake(Snake* snake) {
    // Eat food
    if (snake->head->x == get_food()->x && snake->head->y == get_food()->y) {
        snake->length++;
        score++;
        spawn_food();
    }
    // Move head
    Point* new_head = snake->head + 1;
    new_head->x = snake->head->x;
    new_head->y = snake->head->y;
    // Update tail
    snake->tail = snake->head;
    snake->head++;
}

// Handle user input
void handle_input() {
    // Get user input
    SDL_Event event;
    while (SDL_PollEvent(&event)) {
        if (event.type == SDL_QUIT) {
            game_over = true;
        } else if (event.type == SDL_KEYDOWN) {
            switch (event.key.keysym.sym) {
                case SDLK_UP:
