                    break;
                case SDLK_DOWN:
                    // Move snake down
                    break;
                case SDLK_LEFT:
                    // Move snake left
                    break;
                case SDLK_RIGHT:
                    // Move snake right
                    break;
            }
        }
    }
}

// Spawn food
void spawn_food() {
    // Randomly select a point on the board
    Point* food = (Point*)malloc(sizeof(Point));
    food->x = rand() % GAME_WIDTH;
    food->y = rand() % GAME_HEIGHT;
    // Check if the food is on the snake or outside the board
