client.commands = new Discord.Collection();

client.commands.set('ban', {
  name: 'ban',
  description: 'Ban a member from the server',
  execute: async (interaction) => {
    const member = interaction.options.getMember('user');
    if (!member) return interaction.reply({ content: 'Invalid user', ephemeral: true });

    const reason = interaction.options.getString('reason');

    if (!interaction.member.permissions.has('BanMembers')) return interaction.reply({ content: 'You do not have the permission to ban members', ephemeral: true });

    if (member.permissions.has('Administrator')) return interaction.reply({ content: 'Cannot ban the server administrator', ephemeral: true });

    try {
      await member.ban({ reason: reason || 'No reason provided' });
      interaction.reply({ content: `${member} has been banned`, ephemeral: false });
    } catch (error) {
      console.error(error);
      interaction.reply({ content: 'Failed to ban the member', ephemeral: true });
    }
  }
});

client.on('ready', () => {
  console.log('Ready!');
  client.application.commands.set(client.commands.cache);
});

client.login('YOUR_TOKEN');
```

