  // Add the image snapshot plugin
  on('task', addMatchImageSnapshotCommand);

  // Set up Docker
  config.env.DOCKER_CONTAINER = 'my-test-app';
  config.env.DOCKER_PORT = 8080;

  // Set up Cypress settings
  config.baseUrl = 'http://localhost:8080';
  config.video = false;
  config.screenshotOnRunFailure = true;
