#include "game.h"
#include "snake.h"
#include "level.h"
#include "assets.h"

int main() {
    // Initialize SDL
    SDL_Init(SDL_INIT_VIDEO | SDL_INIT_EVENTS);

    // Create the window
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, 640, 480, 0);
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    // Load assets
    Snake* snake = snake_new();
    Level* level = level_load("level1");

    // Main game loop
    while (running) {
        // Handle events
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                running = 0;
            } else if (event.type == SDL_KEYDOWN) {
                // Update snake direction
                if (event.key.keysym.sym == SDLK_UP) {
                    snake->direction = up;
                } else if (event.key.keysym.sym == SDLK_DOWN) {
                    snake->direction = down;
                } else if (event.key.keysym.sym == SDLK_LEFT) {
                    snake->direction = left;
                } else if (event.key.keysym.sym == SDLK_RIGHT) {
                    snake->direction = right;
                }
            }
        }

        // Update snake movement and collision detection
        snake_update(snake, level);

        // Clear screen and draw everything
        SDL_RenderClear(renderer);
        level_draw(level, renderer);
        snake_draw(snake, renderer);
        SDL_RenderPresent(renderer);
    }

    // Clean up
    snake_free(snake);
    level_free(level);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
    return 0;
}
