    if (num <= 1) return false;
    for (let i = 2; i * i <= num; i++)
        if (num % i === 0) return false;
    return true;
}

function checkArray(arr) {
    for (let num of arr) {
        if (isPrime(num)) {
            console.log(num + " is a prime number.");
        }
    }
}

