```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <map>

using namespace std;

int countWordOccurrences(const string& filename, const string& word) {
    int occurrences = 0;
    map<string, int> wordCount;

    ifstream file(filename);
    if (!file.is_open()) {
        cerr << "Error opening file: " << filename << endl;
        return -1;
    }

    string line;
    while (getline(file, line)) {
        string currentWord;
        istringstream iss(line);
        while (iss >> currentWord) {
            if (wordCount.find(currentWord) != wordCount.end()) {
                wordCount[currentWord]++;
            } else {
                wordCount[currentWord] = 1;
            }
        }
    }

    file.close();

    return wordCount[word];
}

int main() {
    string filename = "example.txt"; // Replace with your file name
    string word = "exampleWord"; // Replace with your word

    int occurrences = countWordOccurrences(filename, word);
    if (occurrences == -1) {
        return 1;
    }

    cout << "The word '" << word << "' occurs " << occurrences << " times in the file." << endl;

    return 0;
}
```

