var speed = 5.0
var mouse_sensitivity = 0.05

var velocity = Vector3.ZERO

func _ready():
    $Camera.make_current()

func _process(delta):
    var movement = Vector3.ZERO

    if Input.is_action_pressed("ui_right"):
        movement += Vector3.RIGHT * speed
    if Input.is_action_pressed("ui_left"):
        movement += Vector3.LEFT * speed
    if Input.is_action_pressed("ui_down"):
        movement += Vector3.BACK * speed
    if Input.is_action_pressed("ui_up"):
        movement += Vector3.FORWARD * speed

    movement = movement.normalized()

    if is_on_floor():
        velocity = move_and_slide(movement, Vector3.UP)
    else:
        velocity = move_and_slide(movement)

    $Camera.add_rotation_from_basis(camera.transform.basis)
    $Camera.transform.origin += velocity * delta

func _input(event):
