#include <regex>
#include <string>

std::string markdownToHtml(const std::string& markdown) {
    std::string html;
    std::regex italic("_(.*?)_");
    std::regex italicLink("_(?:https?|ftp|file):\/\/([^\s]+)_");
    std::regex bold("__(.*?)__");
    std::regex boldLink("__(?:https?|ftp|file):\/\/([^\s]+)__");
    std::regex header("^# (.*)$", std::regex_constants::multiline);
    std::regex link("(?:https?|ftp|file):\/\/([^\s]+)");
    std::regex unorderedList("^ *([*+]- )(.*)");
    std::regex orderedList("^ *[0-9]+\\. (.*)");
    std::regex strikethrough("~~(.*?)~~");
    std::regex escapeChars("([*_{}[]()<>`\\|=.+?)]");

    html = std::regex_replace(markdown, italic, "<i>$1</i>");
    html = std::regex_replace(html, italicLink, "<a href=\"$1\">$1</a>");
    html = std::regex_replace(html, bold, "<b>$1</b>");
    html = std::regex_replace(html, boldLink, "<a href=\"$1\">$1</a>");
    html = std::regex_replace(html, header, "<h1>$1</h1>");
    html = std::regex_replace(html, link, "<a href=\"$1\">$1</a>");
    html = std::regex_replace(html, unorderedList, "<ul><li>$2</li></ul>");
    html = std::regex_replace(html, orderedList, "<ol><li>$1</li></ol>");
    html = std::regex_replace(html, strikethrough, "<s>$1</s>");
    html = std::regex_replace(html, escapeChars, "<$1>");

    return html;
}
```

This function supports the following markdown syntax:

* **Bold text**: `**text**`
* **Italic text**: `*text*`
* **Links**: `[text](http://www.example.com)`
* **Headers**: `# Heading`
* **Unordered lists**: `- item 1`, `- item 2`, ...
* **Ordered lists**: `1. item 1`, `2. item 2`, ...
* **Strikethrough**: `~~text~~`
* **Escape characters**: `*`, `_`, `**`, `[]`, `()`, `<`, `>`, `\`, `|`, `=`

