#include <string>
#include <regex>
#include <iostream>

std::string markdownToHTML(const std::string& markdown) {
    std::string html;
    std::string str = markdown;

    // Headers
    str = std::regex_replace(str, std::regex("^# (.*)"), "<h1>$1</h1>");
    str = std::regex_replace(str, std::regex("^## (.*)"), "<h2>$1</h2>");
    str = std::regex_replace(str, std::regex("^### (.*)"), "<h3>$1</h3>");
    str = std::regex_replace(str, std::regex("^#### (.*)"), "<h4>$1</h4>");
    str = std::regex_replace(str, std::regex("^##### (.*)"), "<h5>$1</h5>");
    str = std::regex_replace(str, std::regex("^###### (.*)"), "<h6>$1</h6>");

    // Bold
    str = std::regex_replace(str, std::regex("__([^_]+?)__"), "<b>$1</b>");
    str = std::regex_replace(str, std::regex("[\*]{2}([^\\*]+?)[\*]{2}", std::regex::ECMAScript), "<i>$1</i>");

    // Italic
    str = std::regex_replace(str, std::regex("([^\\*]+?)\*" , std::regex::ECMAScript), "<i>$1</i>");
    str = std::regex_replace(str, std::regex("\\*([^\\*]+?)\*" , std::regex::ECMAScript), "<i>$1</i>");

    // Links
    str = std::regex_replace(str, std::regex("\\[(.*)\\]\\(([^\\)]+)\\)", std::regex::ECMAScript), "<a href=\"$2\">$1</a>");

    // Lists
    str = std::regex_replace(str, std::regex("^ *([0-9]+) .*$", std::regex::ECMAScript), "<ol><li>$1</li>");
    str = std::regex_replace(str, std::regex("^ *([xX][ivV]) .*$", std::regex::ECMAScript), "<ul><li>$1</li>");
    str = std::regex_replace(str, std::regex("^ *([-+]?[0-9]+) .*$", std::regex::ECMAScript), "<ol><li>$1</li>");

    // Bold and Italic
    str = std::regex_replace(str, std::regex("__([^_]+?)__([^_]+?)__"), "<b><i>$1</i></b>");
    str = std::regex_replace(str, std::regex("[\*]{2}([^\\*]+?)[\*]{2}([^\\*]+?)[\*]{2}", std::regex::ECMAScript), "<i><b>$1</b></i>");

    // Code
    str = std::regex_replace(str, std::regex("`([^`]+?)`"), "<code>$1</code>");

    // Code block
    str = std::regex_replace(str, std::regex("```(.*)```"), "<pre><code>$1</code></pre>");

    return str;
}

int main() {
    std::string markdown = "# Hello World\n" +
                            "This is an example of **bold** and _italic_ text.\n" +
                            "You can also use [links](https://www.example.com).\n" +
                            "Here is a list:\n" +
                            "  1. Item 1\n" +
                            "  2. Item 2\n" +
                            "And here is some code: `print('Hello World');`\n" +
                            "And here is a code block:\n" +
                            "```python\n" +
                            "print('Hello World')\n" +
                            "```";

    std::cout << markdownToHTML(markdown) << std::endl;

    return 0;
}
```

