#include <regex>
#include <string>

std::string markdownToHtml(const std::string& markdown) {
    std::string html = markdown;

    // Headers
    html = std::regex_replace(html, std::regex("^# (.*)$", "<h1>$1</h1>");
    html = std::regex_replace(html, std::regex("^## (.*)$", "<h2>$1</h2>");
    html = std::regex_replace(html, std::regex("^### (.*)$", "<h3>$1</h3>");
    html = std::regex_replace(html, std::regex("^#### (.*)$", "<h4>$1</h4>");
    html = std::regex_replace(html, std::regex("^##### (.*)$", "<h5>$1</h5>");
    html = std::regex_replace(html, std::regex("^###### (.*)$", "<h6>$1</h6>");

    // Bold and Italic
    html = std::regex_replace(html, std::regex("__(.*)__", std::regex::ECMAScript), "<strong>$1</strong>");
    html = std::regex_replace(html, std::regex("~~(.*)~~", std::regex::ECMAScript), "<s>$1</s>");
    html = std::regex_replace(html, std::regex("*(.*)\*", std::regex::ECMAScript), "<em>$1</em>");
    html = std::regex_replace(html, std::regex("_(.*)_", std::regex::ECMAScript), "<i>$1</i>");

    // Links
    html = std::regex_replace(html, std::regex("[(](.*)[)]", std::regex::ECMAScript), "<a href=\"$1\">$1</a>");

    // Lists
    html = std::regex_replace(html, std::regex("^(.*)\n\n", std::regex::ECMAScript), "<p>$1</p>");
    html = std::regex_replace(html, std::regex("^\\* (.*)", std::regex::ECMAScript), "<li>$1</li>");
    html = std::regex_replace(html, std::regex("^\\[(.*)\\]\\(.*\\)", std::regex::ECMAScript), "<li><a href=\"$2\">$1</a></li>");

    return html;
}
```

