```cpp
#include <string>
#include <regex>
#include <iostream>

std::string markdownToHTML(const std::string& markdown) {
    std::string html;
    std::string pattern;
    std::string replacement;

    // Headers
    pattern = "^# (.+)$";
    replacement = "<h1>$1</h1>";
    html = std::regex_replace(markdown, std::regex(pattern), replacement);

    // Bold
    pattern = "__(.+?)__";
    replacement = "<b>$1</b>";
    html = std::regex_replace(html, std::regex(pattern), replacement);

    // Italic
    pattern = "_(.+?)_";
    replacement = "<i>$1</i>";
    html = std::regex_replace(html, std::regex(pattern), replacement);

    // Links
    pattern = "\\[(.+?)\\]\\((.+?)\\)";
    replacement = "<a href=\"$2\">$1</a>";
    html = std::regex_replace(html, std::regex(pattern), replacement);

    // Lists
    pattern = "^\\* (.+)$";
    replacement = "<li>$1</li>";
    std::string list;
    size_t pos = 0;
    while ((pos = html.find(pattern)) != std::string::npos) {
        list += html.substr(0, pos);
        html = html.substr(pos + 1);
        pattern = "^\\* (.+)$";
        replacement = "<ul><li>$1</li></ul>";
        html = std::regex_replace(html, std::regex(pattern), replacement);
    }
    if (!html.empty()) {
        list += html;
    }
    html = list;

    return html;
}

int main() {
    std::string markdown = "# Hello **World**\n"
                           "This is *italic text*\n"
                           "Visit the [Google website](http://www.google.com)\n"
                           "* Item 1\n* Item 2\n* Item 3";
    std::cout << markdownToHTML(markdown) << std::endl;
    return 0;
}
```

