    if (!member) {
      return interaction.reply({ content: 'User not found in this guild.', ephemeral: true });
    }

    if (interaction.memberPermissions) {
      if (!interaction.memberPermissions.has(PermissionsBitField.Flags.BanMembers)) {
        return interaction.reply({ content: 'I cannot ban users without the necessary permissions.', ephemeral: true });
      }
    } else {
      // Handle non-DM interaction (e.g., in a guild text channel)
      // ... (implement additional checks, if needed)
    }

    await member.ban({ reason: reason });
    interaction.reply({ content: `${user} has been banned for ${reason}.`, ephemeral: false });
  },
});

client.login('YOUR_DISCORD_BOT_TOKEN');

// Replace 'YOUR_DISCORD_BOT_TOKEN' with your actual bot token
```

