Here's a simple C++ program that reads a text file line by line and counts the occurrences of a specific word:

```cpp
#include <iostream>
#include <fstream>
#include <sstream>
#include <string>

using namespace std;

int countWordOccurrences(const string& filename, const string& targetWord) {
    int count = 0;
    string line, word;

    ifstream file(filename);
    if (!file) {
        cerr << "Error opening file." << endl;
        return -1; // return error code
    }

    while (getline(file, line)) {
        istringstream lineStream(line);
        string token;
        while (getline(lineStream, token, ' ')) { // split by spaces
            if (token == targetWord) {
                count++; // increment count if the token is the target word
            }
        }
    }
    file.close();
    return count;
}

int main() {
    string filename = "input.txt"; // replace with your file name
    string targetWord = "hello"; // word to search for

    int occurrences = countWordOccurrences(filename, targetWord);
    if (occurrences >= 0) {
        cout << "Word '" << targetWord << "' occurs " << occurrences << " times in " << filename << "." << endl;
    }
    return 0;
}
```

