FROM cypress/included:8.5.0

# Set working directory
WORKDIR /e2e

# Copy code and cypress configuration
COPY cypress /e2e
COPY package*.json ./

RUN npm install

# Expose and run Cypress
EXPOSE 4444
CMD ["cypress run --headless --config cabbage.json"]
```
