  await client.options.commands.registerApplicationCommandsDevelopMode();
  await client.commands.cache.refresh();
}
registerCommands();

client.once('ready', () => {
  console.log('Bot is ready!');
  registerCommands(); // Re-run on ready, ensuring commands are registered
});

client.on('interactionCreate', async (interaction) => {
  if (interaction.isChatInputCommand() && interaction.commandName === 'ban') {
    const member = interaction.options.getMember('target');
    const reason = interaction.options.getString('reason', true); // default to empty string if not provided
