    SDL_RenderClear(game->renderer);

    // Render snake segments
    for (int i = 0; i < game->snake_length; i++) {
        SDL_Texture* texture = SDL_CreateTextureFromSurface(game->renderer, // load and create texture for current frame
            SDL_LoadBMP(SNAKE_IMAGE_PATH "snake_body_" + i % SNAKE_FRAME_RATE + ".png"));
