```javascript
const { Client, GatewayIntentBits } = require('discord.js');
const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMembers, GatewayIntentBits.DirectMessages, GatewayIntentBits.GatewayPresences, GatewayIntentBits.MessageContent] });

client.commands = new Map();
client.rest = require('@discordjs/rest');
const rest = new client.rest({ version: '10' );

// Register Commands
const commands = [
  {
    name: 'ban',
    description: 'Bans a user from the server.',
    options: [
      {
        name: 'user',
        description: 'The user to ban',
        type: 'user',
        required: true,
      },
      {
        name: 'days',
        description: 'Number of days to ban (optional, default 7)',
        type: 'integer',
        required: false,
      },
    ],
    defaultMemberPermissions: Discord.PermissionsBitField.Flags.BanMembers,
    ephemeral: true,
  },
];
const commandData = commands.map((command) => ({ ...command, dataType: '1' })); // Not needed but required by the V14               (JSON payload format)
const scanning GuildCommands = rest.put(
  process.env.DISCORD_API_URL `/guilds/${process.env.GUILD_ID}/commands`,
  { body: commandData },
);

scanning GuildCommands.then(() => console.log('Commands registered'));

client.on('ready', () => {
  console.log('Bot is online!');
});

client.on('interactionCreate', async (interaction) => {
  if (!interaction.isSlash()) return; // Check if it's a slash command
  if (interaction.commandName === 'ban') {
    const user = interaction.options.getUser('user');
    const days = interaction.options.get('days', true) ? interaction.options.getInteger('days') || 7 : 7;
    const member = interaction.guild.members.cache.get(user.id);
    if (!member) {
      await interaction responds({ content: `Couldn't find member`, ephemeral: true });
      return;
    }
    try {
      await member.ban({ days: days });
      await interaction responds({ content: `${user} has been banned for ${days} day(s)`, ephemeral: true });
    } catch (error) {
      await interaction.respond({ content: 'Error banning user, check permissions or checks.', ephemeral: true });
    }
  }
});

client.login(process.env.TOKEN);
```

