  \begin{tikzpicture}
    \def\size{8} % size of the chessboard
    \def\sqsize{\size*0.8cm} % size of a single square
    \draw (0,0) grid (\size,\size);
    \foreach \x in {1,...,\size} {
      \foreach \y in {1,...,\size} {
        \ifthenelse{\mod(\x+1,\size)=1||\mod(\y+1,\size)=1}{%
          \fill[fill=white!50] (\x*\sqsize-0.4\sqsize,\y*\sqsize-0.4\sqsize) rectangle ++(\sqsize);
        }{%
          \fill[fill=white!50] (\x*\sqsize-0.4\sqsize,\y*\sqsize-0.4\sqsize) rectangle ++(\sqsize);
        }
      }
    }
    % add your FEN notation pieces here (assuming you'll provide them in the #1 argument)
    \foreach \pos in {#1} {
      \path (\struteheight-\fboxsep-\fboxrule+\pos.x*\sqsize,\pos.y*\sqsize) node{\piece{\pos.piecename}};
    }
  \end{tikzpicture}%
}

\newcommand{\piece}[2]{%
  \edef\piecename{#2}%
  \ifthenelse{\piecename=r}{\tikzshape{circle[radius=0.3\sqsize]}}
