    snake.dir = SNAKE_DIR_RIGHT;
    stats.score = 0;
    stats.level = 1;
}

void update_game(SnakeSegment *snake, GameStats *stats) {
    // Update snake movement
    if (snake->dir == SNAKE_DIR_UP) {
        snake->y -= SNAKE_SPEED;
    } else if (snake->dir == SNAKE_DIR_DOWN) {
        snake->y += SNAKE_SPEED;
    } else if (snake->dir == SNAKE_DIR_LEFT) {
        snake->x -= SNAKE_SPEED;
    } else if (snake->dir == SNAKE_DIR_RIGHT) {
        snake->x += SNAKE_SPEED;
    }

    // Check collision with wall or self
    check_collision(snake, stats);
}

void render_game(SDL_Renderer *renderer, SnakeSegment *snake, GameStats *stats) {
    // Clear screen
    SDL_RenderClear(renderer);

    // Draw background
    SDL_RenderCopy(renderer, background_texture, NULL, NULL);

    // Draw snake
    for (int i = 0; i < snake.length; i++) {
        SDL_Rect rect = { snake[i].x, snake[i].y, 10, 10 };
        SDL_RenderCopy(renderer, snake_texture, NULL, &rect);
    }

    // Draw food
    SDL_Rect food_rect = { food_x, food_y, 10, 10 };
    SDL_RenderCopy(renderer, food_texture, NULL, &food_rect);

    // Draw score and level
    SDL_Rect score_rect = { 10, 10, 100, 20 };
    SDL_RenderFillRect(renderer, &score_rect);
    SDL_RenderCopy(renderer, font_texture, NULL, &score_rect);

    // Present the frame
    SDL_RenderPresent(renderer);
}

void handle_input(SnakeSegment *snake, int *dir) {
    // Handle keyboard input
    SDL_Event event;
    while (SDL_PollEvent(&event)) {
        if (event.type == SDL_KEYDOWN) {
            if (event.key.keysym.sym == SDLK_UP && snake->dir != SNAKE_DIR_DOWN) {
                *dir = SNAKE_DIR_UP;
            } else if (event.key.keysym.sym == SDLK_DOWN && snake->dir != SNAKE_DIR_UP) {
                *dir = SNAKE_DIR_DOWN;
            } else if (event.key.keysym.sym == SDLK_LEFT && snake->dir != SNAKE_DIR_RIGHT) {
                *dir = SNAKE_DIR_LEFT;
            } else if (event.key.keysym.sym == SDLK_RIGHT && snake->dir != SNAKE_DIR_LEFT) {
                *dir = SNAKE_DIR_RIGHT;
            }
        }
    }
}

void check_collision(SnakeSegment *snake, GameStats *stats) {
    // Check collision with wall
    if (snake->x < 0 || snake->x > SCREEN_WIDTH || snake->y < 0 || snake->y > SCREEN_HEIGHT) {
        game_over();
    }

    // Check collision with self
    for (int i = 1; i < snake.length; i++) {
        if (snake[i].x == snake[0].x && snake[i].y == snake[0].y) {
            game_over();
        }
    }
}

void game_loop(SDL_Window *window, SDL_Renderer *renderer, SnakeSegment *snake, GameStats *stats) {
    int dir = SNAKE_DIR_RIGHT;
    bool running = true;

    while (running) {
        handle_input(snake, &dir);
        update_game(snake, stats);
        render_game(renderer, snake, stats);

        // Cap frame rate at 30 FPS
        SDL_Delay(1000 / 30);

        // Check for game over
        if (stats.score >= 10) {
            // Level up! Increase score and reset snake
            stats.score += 10;
            stats.level++;
            snake.length = 1;
            snake[0].x = SCREEN_WIDTH / 2;
            snake[0].y = SCREEN_HEIGHT / 2;
        }
    }

    // Clean up
    SDL_DestroyTexture(snake_texture);
    SDL_DestroyTexture(food_texture);
    SDL_DestroyTexture(background_texture);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
